syntax-example:
/* &lt;length&gt; values */
block-size: 300px;
block-size: 25em;

/* &lt;percentage&gt; values */
block-size: 75%;

/* Keyword values */
block-size: max-content;
block-size: min-content;
block-size: fit-content(20em);
block-size: auto;

/* Global values */
block-size: inherit;
block-size: initial;
block-size: revert;
block-size: revert-layer;
block-size: unset;
