syntax-example:
/* One &lt;length&gt; value */
gap: 20px;
gap: 1em;
gap: 3vmin;
gap: 0.5cm;

/* One &lt;percentage&gt; value */
gap: 16%;
gap: 100%;

/* Two &lt;length&gt; values */
gap: 20px 10px;
gap: 1em 0.5em;
gap: 3vmin 2vmax;
gap: 0.5cm 2mm;

/* One or two &lt;percentage&gt; values */
gap: 16% 100%;
gap: 21px 82%;

/* calc() values */
gap: calc(10% + 20px);
gap: calc(20px + 10%) calc(10% - 5px);

/* Global values */
gap: inherit;
gap: initial;
gap: revert;
gap: revert-layer;
gap: unset;
