syntax-example:
/* &lt;length&gt; values */
height: 120px;
height: 10em;
height: 100vh;

/* &lt;percentage&gt; value */
height: 75%;

/* Keyword values */
height: max-content;
height: min-content;
height: fit-content(20em);
height: auto;

/* Global values */
height: inherit;
height: initial;
height: revert;
height: revert-layer;
height: unset;
