syntax-example:
/* &lt;length&gt; value */
min-height: 3.5em;

/* &lt;percentage&gt; value */
min-height: 10%;

/* Keyword values */
min-height: max-content;
min-height: min-content;
min-height: fit-content(20em);

/* Global values */
min-height: inherit;
min-height: initial;
min-height: revert;
min-height: revert-layer;
min-height: unset;
