syntax-example:
/* &lt;length&gt; value */
min-width: 3.5em;

/* &lt;percentage&gt; value */
min-width: 10%;

/* Keyword values */
min-width: max-content;
min-width: min-content;
min-width: fit-content(20em);

/* Global values */
min-width: inherit;
min-width: initial;
min-width: revert;
min-width: revert-layer;
min-width: unset;
