syntax-example:
/* Keyword value */
quotes: none;
quotes: auto;

/* &lt;string&gt; values */
quotes: &quot;«&quot; &quot;»&quot;; /* Set open-quote and close-quote to the French quotation marks */
quotes: &quot;«&quot; &quot;»&quot; &quot;‹&quot; &quot;›&quot;; /* Set two levels of quotation marks */

/* Global values */
quotes: inherit;
quotes: initial;
quotes: revert;
quotes: revert-layer;
quotes: unset;
