syntax-example:
/* &lt;length&gt; values */
text-indent: 3mm;
text-indent: 40px;

/* &lt;percentage&gt; value
   relative to the containing block width */
text-indent: 15%;

/* Keyword values */
text-indent: 5em each-line;
text-indent: 5em hanging;
text-indent: 5em hanging each-line;

/* Global values */
text-indent: inherit;
text-indent: initial;
text-indent: revert;
text-indent: revert-layer;
text-indent: unset;
